import React from "react";

function GroceryItem({ index, item, dispatch }) {
  return (
    <div key={index}>
      <span>{item.value}</span>&nbsp;<span>{item.qty}</span>
      <button onClick={() => dispatch(item.id)}>Remove</button>
    </div>
  );
}

export default GroceryItem;
