import * as actionTypes from "../actions/actionTypes";
function groceryReducer(state = [], action) {
  console.log("state", state, "action", action);
  switch (action.type) {
    case actionTypes.ADD_GROCERY:
      // const existingGrocery = state.filter(
      //   (el) => el.value === action.value.value
      // );
      // return existingGrocery.length
      //   ? [...state, { ...existingGrocery, qty: existingGrocery[0].qty++ }]
      //   : [...state, action.value];
      let exist = false;
      const newState = state.map((el) => {
        if (el.value === action.value.value) {
          exist = true;
          return { ...el, qty: el.qty++ };
        } else {
          return el;
        }
      });
      return newState.length ? newState : [...state, action.value];
    case actionTypes.REMOVE_GROCERY:
      return state.filter((el) => el.value !== action.value);
    case actionTypes.CLEAR_GROCERY_LIST:
      return (state = []);
    default:
      return state;
  }
}

export default groceryReducer;
