import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { ClearGroceryList, RemoveGrocery } from "../../actions";
function GroceryList() {
  const groceryState = useSelector((state) => state);
  const dispatch = useDispatch();
  const clearGroceryList = () => dispatch(ClearGroceryList());
  const removeGroceryFromList = (grocery) => dispatch(RemoveGrocery(grocery));
  console.log("groceryState", groceryState);
  return (
    <div>
      <h3>Grocery List</h3>
      <button onClick={() => clearGroceryList()}>Clear List</button>
      {groceryState.map((el, index) => {
        return (
          <div key={index}>
            <span>{el.value}</span>&nbsp;<span>{el.qty}</span>
            <button onClick={() => removeGroceryFromList(el.value)}>
              Remove
            </button>
          </div>
        );
      })}
    </div>
  );
}

export default GroceryList;
