import * as actionTypes from "./actionTypes";
let groceryQty = 1;
export const AddGrocery = (value) => ({
  type: actionTypes.ADD_GROCERY,
  value: { value, qty: ++groceryQty },
});

export const RemoveGrocery = (value) => ({
  type: actionTypes.REMOVE_GROCERY,
  value: value,
});
